const dropdowns=document.querySelectorAll(".dropdown");dropdowns.forEach(e=>{const t=e.querySelector(".select"),c=e.querySelector(".caret"),s=e.querySelector(".menu"),r=e.querySelectorAll(".menu li"),l=e.querySelector(".selected");t.addEventListener("click",()=>{t.classList.toggle("select-clicked"),c.classList.toggle("caret-rotate"),s.classList.toggle("menu-open")}),r.forEach(e=>{e.addEventListener("click",()=>{l.innerText=e.innerText,t.classList.remove("select-clicked"),c.classList.remove("caret-rotate"),s.classList.remove("menu-open"),r.forEach(e=>{e.classList.remove("active")}),e.classList.add("active")})})});